import React from 'react';
import '../collection-preview/collection-preview.styles.scss';
import CollectionItem from '../../components/collection-item/collection-item.component';

const CollectionPreview = ({key,routeName,title,items})=>
{
    return (
        <div className="collection-preview">
            <h1 className="title">{title.toUpperCase()}</h1>      
                <div className="preview">
                    {
                        items
                        .filter(({id,...otherCollectionItemProps},idx) => idx < 4)
                        .map(({id,...otherCollectionItemProps})=>(
                            <CollectionItem key={id} {...otherCollectionItemProps}/>
                            ))    
                    }

                </div>






        </div>
    );
}
export default CollectionPreview;
