import React from 'react';
import { googleSignInStart , emailSignInStart } from '../../redux/user/user.actions';
import FormInput from '../form-input/form-input.component';
import CustomButton from '../custom-button/custom-button.component';

import {connect} from 'react-redux';
import {
    SignInContainer,
    ButtonsContainer,
    TitleContainer
    } from './sign-in.styles';  

class SignIn extends React.Component
{
    constructor(props)
    {
        super(props);
        this.state={
            email:'',
            password:''
        }
    }

    handleSubmit= async (event) =>{
        event.preventDefault();

        const {email , password} = this.state;
        const {emailSignInStart} = this.props;
        emailSignInStart(email ,password);
        this.setState({email:'', password:''});
    }

    handleChange = (event) => {

        const {value , name} = event.target;
        this.setState({[name] : value});
        event.preventDefault();
    }
    render(){
        const {googleSignInStart} = this.props;

        return (
            <SignInContainer>
                <TitleContainer>I already have an account</TitleContainer>
                <span>Sign In with email & password </span>

                <form action="" method="post" onSubmit={this.handleSubmit}>

                    <FormInput type="email" id="email" name="email"  value={this.state.email} required label="Email" handleChange={this.handleChange} />
                    <FormInput type="password" name="password" id="password" value={this.state.password} required label="Password" handleChange={this.handleChange}/>
                    <ButtonsContainer>
                        <CustomButton type="submit">SIGN IN</CustomButton>
                        <CustomButton type="button" onClick={googleSignInStart} isGoogleSignIn >

                            Sign In With Google
                        </CustomButton>
                    </ButtonsContainer>
                </form>

            </SignInContainer>
        )
    }
}

const mapDispatchToProps = dispatch =>({
    googleSignInStart:() => dispatch(googleSignInStart()),
    emailSignInStart:(email , password) => dispatch(emailSignInStart({email , password}))
})
export default connect(null , mapDispatchToProps)(SignIn);
