import React from 'react';
import { fetchCollectionsStartAsync } from '../../redux/shop/shop.action';
import { connect } from 'react-redux';

import CollectionItem from '../../components/collection-item/collection-item.component';
import {CollectionContainer , TitleContainer , ItemsContainer} from './collection.styles';
import {selectCollection} from '../../redux/shop/shop.selector'; 

class CollectionPage extends React.Component{

    componentDidMount(){
        const { fetchCollectionsStartAsync} = this.props;
        fetchCollectionsStartAsync();
    }
    render(){
        const collectionData = this.props.collection;
        //const {title , items} = collectiondata;


        return (
            <CollectionContainer>
                <TitleContainer>{collectionData.title}</TitleContainer>
                <ItemsContainer>
                    {
                        collectionData.items.map((item)=>(
                            <CollectionItem key={item.id} item={item}/>
                        ))
                    }
                </ItemsContainer>
            </CollectionContainer>
        );
    }
}

const mapStateToProps = (state , ownProps) => ({
    collection : selectCollection(ownProps.match.params.collectionId)(state)
})


const mapDispatchToProps = dispatch =>({
    fetchCollectionsStartAsync : () => dispatch(fetchCollectionsStartAsync())
})
export default connect(mapStateToProps , mapDispatchToProps)(CollectionPage);
