import React from 'react';
import './form-input.styles.scss';

const FormInput = ({name,value,type,id,handleChange}) => (
    <div className={name}>
        <input type={type} id={id} name={name}  value={value} required onChange={handleChange} required/>
        <label For={type}>{name.charAt(0).toUpperCase() + name.slice(1)}</label>
    </div>
)

export default FormInput;
