import React from 'react';
import {Route , Switch , Redirect} from 'react-router-dom';
import './App.css';
import HomePage from './pages/homepage/homepage.component';
import ShopPage from '../src/pages/shop/shop.component';
import Header from '../src/components/header/header.component';
import SignInAndSignUp from '../src/pages/sign-in-and-sign-up/sign-in-and-sign-up.component';
import {auth , createUserProfileDocument } from './firebase/firebase.utils';
import {setCurrentUser} from './redux/user/user.actions';
import { selectCurrentUser } from './redux/user/user.selector';
import {connect} from 'react-redux';
import {createStructuredSelector} from 'reselect';
import CheckoutPage from './pages/checkout/checkout.component';
import WithSpinner from './components/with-spinner/with-spinner.component';
import CollectionPage from './pages/collection/collection.component';
import { selectIsCollectionFetching } from './redux/shop/shop.selector';

const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class App extends React.Component {
  unsubscribeFromAuth = null;

  componentDidMount(){
    const {setCurrentUser} = this.props;
    this.unsubscribeFromAuth = auth.onAuthStateChanged(async userAuth => {
      if(userAuth)
      {
        const userRef = await createUserProfileDocument(userAuth);
        userRef.onSnapshot(snapShot =>(
          setCurrentUser({
              id:snapShot.id,
              ...snapShot.data()
            })
          )
        )

      }
      else
        setCurrentUser(userAuth);


    })
  }  
  componentWillUnmount(){
    this.unsubscribeFromAuth();
  }
  render(){
    const {isCollectionFetching} = this.props;
  return (
    <div className="App">
      <Header/>
      <Switch>
        <Route exact path='/' component={HomePage} />
        <Route exact path='/shop' component={ShopPage} />
        <Route path='/shop/:collectionId' render={(props) => <CollectionPageWithSpinner isLoading={isCollectionFetching} {...props}/>} />
        <Route exact path='/checkout' component={CheckoutPage} />
        <Route exact path='/signin' render={() => this.props.currentUser ? (<Redirect to="/" />) : <SignInAndSignUp />} />
      </Switch>
    </div>
  );
  }
}

const mapStateToProps = createStructuredSelector(
  {
    currentUser:selectCurrentUser,
    isCollectionLoaded:selectIsCollectionLoaded
  }
)

const mapDispatchToProps = dispatch =>({
  setCurrentUser: user => dispatch(setCurrentUser(user))

})

export default connect(mapStateToProps,mapDispatchToProps)(App);
