import React from 'react';
import './sign-in.styles.scss';
import {FormInput} from '../form-input/form-input.component';

class SignIn extends React.Component
{
    constructor(props)
    {
        super(props);
        this.state={
            email:'',
            password:''
        }
    }

    handleSubmit= (event) =>{
        event.preventDefault();
        this.setState({email:'', password:''});
    }

    handleChange = (event) => {

        const {value , name} = event.target;
        this.setState({[name] : value});
        event.preventDefault();
    }
    render(){
        return (
            <div className="sign-in">
                <h2>I already have an account</h2>
                <span>Sign In with email & password </span>

                <form action="" method="post" onSubmit={this.handleSubmit}>
                    {
                    <Forminput type="email" id="email" name="email"  value={this.state.email} required onChange={this.handleChange} />
    }
                    <label For="email">Email</label>
                    <input type="password" name="password" id="password" value={this.state.password} required onChange={this.handleChange}/>
                    <label for="password">Password</label>
                    <input type="submit" value="Submit Form"></input>
                </form>

            </div>
        )
    }


}
export default SignIn;
